// Minimal EmailJS shim for development.
// This file prevents runtime errors when the official SDK is not present.
// For real sending use the official SDK (https://cdn.emailjs.com/) or send via your server.
(function(window){
  if(window.emailjs) return; // don't overwrite real SDK
  window.emailjs = {
    _inited: false,
    init: function(publicKey){
      this._inited = true;
      this._publicKey = publicKey;
      console.info('EmailJS shim initialized (no-op).');
    },
    sendForm: function(){
      return Promise.reject(new Error('EmailJS SDK not present. Replace vendor/email.min.js with the official SDK from https://cdn.emailjs.com/sdk/3.2.0/email.min.js or implement server-side sending.'));
    },
    send: function(){
      return Promise.reject(new Error('EmailJS SDK not present. Replace vendor/email.min.js with the official SDK or implement server-side sending.'));
    }
  };
})(window);
